import React from 'react'
import image1 from "../image.png";
import triple_clicked from "../triple_clicked.png"
import { useState, useRef } from 'react';
import './Upload.css'
import { authFetch } from '../auth';

const Upload = () => {
  const [image, setImage] = useState(null);
  const [filename, setfilename] = useState("");
  const [actualimage, setActual] = useState(null);
  const [uploaded, setUploaded] = useState(null);
  const [clicked, setClicked] = useState(false);
  const token = localStorage.getItem('access_token');
  const [videofile, setVideo] = useState(null);
  const [videourl, setVideoURL] = useState(null);
  const [videoname, setVideoName] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const inputRef = useRef();
  const videoRef = useRef();
  const [pausedFrame, setPausedFrame] = useState();
  const [prevFrame, setPrevFrame] = useState();
  const [nextFrame, setNextFrame] = useState();

  const handleChoose = () => {
    inputRef.current.click();
  };

  const handleVideoPause = () => {
    const video = videoRef.current;
    const currentTime = video.currentTime;

    // Only capture frames if the video is paused and not seeking
    if (video.paused && !video.seeking) {
      const canvas = document.createElement("canvas");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext("2d");

      // Capture the frame at the paused second
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const pausedFrameURI = canvas.toDataURL();
      setPausedFrame(pausedFrameURI);

      // Calculate the time for frames before and after the paused second
      const prevTime = Math.max(currentTime - 1, 0);
      const nextTime = Math.min(currentTime + 1, video.duration);

      // Seek to the time for the previous frame and capture it
      video.currentTime = prevTime;
      video.onseeked = () => {
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        const prevFrameURI = canvas.toDataURL();
        setPrevFrame(prevFrameURI);

        // Seek to the time for the next frame and capture it
        video.currentTime = nextTime;
        video.onseeked = () => {
          context.drawImage(video, 0, 0, canvas.width, canvas.height);
          const nextFrameURI = canvas.toDataURL();
          setNextFrame(nextFrameURI);

          // Restore the original video time
          video.currentTime = currentTime;
          video.onseeked = null; // Reset the onseeked handler
        };
      };
    }
  };

  const handleButtonClick = () => {
    setIsOpen(!isOpen);
  };

  const div1_style = {
    position: "relative",
    backgroundImage: "linear-gradient(to right, #000000 5%, #3533cd)",
    minHeight : "100vh",
    minWidth: "100vw",
    top: "-2.2vh",
  };

  const h1_style = {
      position : "relative",
      color : "white",
      top : "5vh",
      textAlign : "center",
  };

  const p_style = {
    position : "relative",
    color : "white",
    top : "4vh",
    textAlign : "center",
  };

  const upload_div = {
    position : "relative",
    border: "3px dotted white",
    borderRadius : "40px",
    height : "74.38vh",
    width : "69.19vw",
    top : "7vh",
    left : "15vw",
  };

  const button1_style = {
    position : "relative",
    borderRadius : "25px",
    border : "0px",
    top : "25vh",
    left : "30.7vw",
    height : "5vh",
    width : "15vh",
    backgroundColor : "white",
  };

  const small_p = {
    position : "relative",
    color : "white",
    top : "25vh",
    fontSize : "10px",
    textAlign : "center",
  };

  const img_container = {
      position : "absolute",
      height : "20vh",
      width : "10vw",
      top : "80vh",
      left : "88vw",
  };

  const img_style = {
      maxWidth : "20vh",
      maxHeight : "20vh",
  };

  /*const triple_img = {
      maxHeight : "100%",
      maxWidth : "100%",
  };*/

  const menu = {
    position : "absolute",
    top : "0vh",
    width: "15vw",
    left: "85vw",
    height: "100vh",
    backgroundImage: "linear-gradient(to left, #000000 1%, #737373)",
    transition: "width 0.00001s ease-in",
  };

  const img_clicked = {
    position : "relative",
    maxHeight: "100%",
    maxWidth : "100%",
  };

  const name = {
    position : "relative",
    color : "white",
    top : "12.5vh",
    fontSize : "24px"
  };

  const form_p = {
    position : "relative",
    top : "1.5vh",
  };

  const image_uploaded = {
    maxHeight : "100%",
    maxWidth : "100%",
    minHeight : "100%",
    minWidth : "100%",
    borderRadius : "40px",
  };

  const handleUpload = async () => {
    setClicked(!clicked);
    console.log(token);
    if (actualimage) {
      try {
        const formData = new FormData();
        formData.append('image', actualimage);

        console.log(formData);

        const response = await authFetch('http://localhost:5000/upload/submit', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
          },
          body: formData,
        });

        if (response.ok) {
          console.log("SENT!")
          //setActual(null); // Clear selected image
          console.log(response)

          getUploaded();
        } else {
          console.error('Error uploading image:', response.statusText);
        }
      } catch (error) {
        console.error('Error communicating with the server', error);
      }
    }
  };

  //let fetch_upload = 'http://10.1.141.170:8000/api/upload/'
  let getUploaded = async () => {
    let response = await authFetch('http://localhost:5000/upload/predict', {
      method : 'GET',
      headers: {
        'Authorization' : `Bearer ${token}`,
      },
    });
    let data = await response.blob();
    const imageUrl = URL.createObjectURL(data);
    setUploaded(imageUrl);
  }

  const video_player = {
    position : "relative",
    minHeight : "100%",
    minWidth : "100%",
    maxHeight : "100%",
    maxWidth : "100%",
    top : "0vh",
    borderRadius : "34px",
  };

  const styles = {
    framesRow: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      margin : "6vh",
    },
    frameImage: {
      maxWidth: "100%",
      border: "1px solid #ccc",
      margin: "0px 5px",
      left : "-10vh",
    },
  };

  const handleimage = (e) =>{
    setActual(e);
    setImage(e);
  }

  localStorage.setItem('filename', filename)

  const path = "/enhance"
  return (
    <body>
      <div style = {div1_style}>
        <h1 style = {h1_style}>Enhance Ai</h1>
        <p style = {p_style}>Upscale your images using Ai</p>
        <div style = {upload_div}>
          {image || videourl ? 
          <>{image ? <>
          <img style = {image_uploaded} src = {image} alt = "HI"/>
          {uploaded ? <a href = {path}><button className = "enhance">Enhance</button></a> : 
           <><button className = "submit" onClick = {handleUpload}>Submit</button>{clicked ? <><div className = "spinner_upload"></div><p className = "loading">Loading</p></> : console.log("NO")}</>}
           </>:<>
               <video
                ref={videoRef}
                style={video_player}
                controls
                src={videourl}
                onPause={handleVideoPause}
              />
               {console.log(localStorage.getItem('videoname'))}
          </>}</>
          :
          <>
          <form className = "form_style" onClick={() => document.querySelector(".input-field").click()}>
            <p style = {form_p}>Upload Image</p>
            <input type="file" accept = "image/.jpg" className = "input-field" id = "input_image" hidden 
            onChange = {({target : {files}}) => {
              files[0] && setfilename(files[0].name)
              if(files)
              {
                setImage(URL.createObjectURL(files[0]));
                setActual(files[0]);
                {if (filename !== ''){localStorage.setItem('filename', filename)}else{console.log("OOPS did not get filename.")}}
              }
            }}/>
          </form>
            <p style = {small_p}>Add a .jpeg or .png file</p>
            <form className = "form_video_style" onClick={() => {document.querySelector(".input_video").click(); handleChoose()}}>
            <p style = {form_p}>Upload Video</p>
            <input ref = {inputRef} type="file" accept=".mov,.mp4" className = "input_video" id = "input_video" hidden 
            onChange = {({target : {files}}) => {
              files[0] && setVideoName(files[0].name)
              if(files)
              {
                setVideoURL(URL.createObjectURL(files[0]));
                setVideo(files[0]);
              }
            }}/>
          </form>
            <p style = {small_p}>Add a .mp4 file</p>
          </>
          }
        </div>
        <div style={styles.framesRow}>
              {pausedFrame && (
                <div>
                  <h2>Frames:</h2>
                  <div style={styles.framesRow}>
                  <img className="frameImage" src={prevFrame} alt="Frame Before" onClick = {() => {handleimage(prevFrame)}}/>
                  <img className="frameImage" src={pausedFrame} alt="Paused Frame" onClick = {() => {handleimage(pausedFrame)}}/>
                  <img className="frameImage" src={nextFrame} alt="Frame After" onClick = {() => {handleimage(nextFrame)}}/>
                  </div>
                </div>
              )}
        </div>
        <div style = {img_container}>
          <img src = {image1} style = {img_style} alt = "robot_image"/>
        </div>
      </div>
      <button className = "u_menu_button" onClick={handleButtonClick}><img style = {img_clicked} src = {triple_clicked} alt = "menu-button"></img></button>
        {isOpen && (<div style = {menu}>
          <button className = "u_menu_button_clicked" onClick={handleButtonClick}><img style = {img_clicked} src = {triple_clicked} alt = "menu-button"></img></button>
          <p style = {name}>Name</p>
          <p style = {name}>Home</p>
          <p style = {name}>Instructions</p>
        </div>)}
    </body>
  );
}

export default Upload
