import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form'


const SignUpPage = () => {


    const { register, handleSubmit, reset, formState: { errors } } = useForm();
    const [show, setShow] = useState(false);
    const [serverResponse, setServerResponse]=useState('');


    const submitForm = (data) => {
        /*to check if the password and confirmPassword are equal*/
        if (data.password === data.confirmPassword) {

            const body = {
                username: data.username,
                email: data.email,
                password: data.password
            }
            /*send the data to server*/
            const requestOptions = {
                method: "POST",
                /*data will be in json format*/
                headers: {
                    'content-type': 'application/json'
                },

                /*body that we are going to send with this data*/
                /*stringify converts the object into json*/
                body: JSON.stringify(body)

            }

            fetch('/auth/signup', requestOptions)
                .then(res => res.json())
                .then(data => {
                    console.log(data)
                    setServerResponse(data.message)
                    console.log(serverResponse)
                    setShow(true)
                })
                .catch(err => console.log(err))

            reset()
        }

        else {

            alert("Passwords do not match")
        }
    }


    const main_div = {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            backgroundImage: "linear-gradient(to right, #000000 5%, #3533cd)", 
            backgroundSize: '200vw 200vh',
            backgroundAttachment: 'fixed',
        }

    const form_div = {
        position : "relative",
        height : "50vh",
        borderRadius: '5px',
        textAlign: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        width: "600px",
        color:"black",
        backgroundColor: "white",
    }

    const signup = {
        position : "relative",
        top : "5vh",
    }

    const username_fill = {
        position : "relative",
        top : "5vh",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
    }

    const email_fill = {
        position : "relative",
        top : "7vh",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
    }

    const password_fill = {
        position : "relative",
        top : "9vh",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
    }

    const confirm_fill = {
        position : "relative",
        top : "11vh",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
    }

    const signup_butt = {
        position : "relative",
        top : "13vh",
        height : "5vh",
    }

    const small = {
        position : "relative",
        top : "15vh",
    }
    return (

        /*styling*/
        <div style={main_div}>
            <div style={form_div}>

                {show?       //to show the alersts, if show is true
                    <>
                      <Alert variant="success" onClose={() => {setShow(false)}} dismissible>
                            <p>
                                {serverResponse}
                            </p></Alert>
                        <h1 style = {signup}>SignUp</h1>
                    </>
                    :
                    <h1 style = {signup}>SignUp</h1>}
                <form>
                    <Form.Group>
                        {/*<Form.Label style = {username}>Username</Form.Label>*/}
                        <Form.Control style = {username_fill} placeholder='Username'
                            type="text"
                            {...register("username", { required: true, maxLength: 25 })}
                        />
                    </Form.Group>
                    {errors.username && <p style={{ position : "relative", color: "red" }}><small>Username is required</small></p>}
                    {errors.username?.type === "maxLength" && <p style={{ position : "relative", color: "red" }}><small>Max characters should be 25</small> </p>}

                    <Form.Group>
                        {/*<Form.Label style = {email}>Email</Form.Label>*/}
                        <Form.Control style = {email_fill} placeholder='Email'
                            type="email"
                            {...register("email", { required: true, maxLength: 80 })}
                        />
                    </Form.Group>
                    {errors.email && <p style={{ position : "relative", color: "red" }}><small>Email is required</small></p>}
                    {errors.email?.type === "maxLength" && <p style={{ position : "relative", color: "red" }}><small>Max characters should be 80</small> </p>}

                    <Form.Group>
                        {/*<Form.Label style = {password}>Password</Form.Label>*/}
                        <Form.Control style = {password_fill} placeholder='Password'
                            type="password"
                            {...register("password", { required: true, minLength: 8 })}
                        />
                    </Form.Group>
                    {errors.password && <p style={{ position : "relative", color: "red" }}> Password is required</p>}
                    {errors.password?.type === "minLength" && <p style={{ position : "relative", color: "red" }}><small>Minimum characters should be 8</small></p>}

                    <Form.Group>
                        {/*<Form.Label style = {password}>Confirm Password</Form.Label>*/}
                        <Form.Control style = {confirm_fill} placeholder='Confirm Password'
                            type="password"
                            {...register("confirmPassword", { required: true, minLength: 8 })}
                        />
                    </Form.Group>
                    {errors.confirmPassword && <p style={{ position : "relative", color: "red" }}><small>Confirm password is required</small></p>}
                    {errors.confirmPassword?.type === "minLength" && <p style={{ position : "relative", color: "red" }}><small>Minimum characters should be 8</small> </p>}

                    <br></br>
                    <Form.Group>
                        <Button style = {signup_butt}as="sub" variant="primary" onClick={handleSubmit(submitForm)}>SignUp</Button>
                    </Form.Group>
                    <br></br>
                    <Form.Group>
                        <small style = {small}>
                            Already have an account?
                            <Link to='/login'>Log In</Link>
                        </small>
                    </Form.Group>
                </form>
            </div>
        </div>
    );
}

export default SignUpPage;
