import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import "./SignUp.css"
import image1 from "../image.png";
import triple_clicked from "../triple_clicked.png";


const SignUpPage = () => {


    const { register, handleSubmit, reset, formState: { errors } } = useForm();
    const [show, setShow] = useState(false);
    const [serverResponse, setServerResponse]=useState('');

    const [isOpen, setIsOpen] = useState(false);

    const handleButtonClick = () => {
      setIsOpen(!isOpen);
    };


    const submitForm = (data) => {
        /*to check if the password and confirmPassword are equal*/
        if (data.password === data.confirmPassword) {

            const body = {
                username: data.username,
                email: data.email,
                password: data.password
            }
            /*send the data to server*/
            const requestOptions = {
                method: "POST",
                /*data will be in json format*/
                headers: {
                    'content-type': 'application/json'
                },

                /*body that we are going to send with this data*/
                /*stringify converts the object into json*/
                body: JSON.stringify(body)

            }

            fetch('/auth/signup', requestOptions)
                .then(res => res.json())
                .then(data => {
                    console.log(data)
                    setServerResponse(data.message)
                    console.log(serverResponse)
                    setShow(true)
                })
                .catch(err => console.log(err))

            reset()
        }

        else {

            alert("Passwords do not match")
        }
    }


    const main_div = {
            position : 'absolute',
            height: '100vh',
            width: '100vw',
            backgroundImage: "linear-gradient(to right, #000000 5%, #3533cd)",
        }

    const form_div = {
        position : "relative",
        height : "55vh",
        border : "5px dotted white",
        borderRadius: '60px',
        textAlign: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        width: "35vw",
        color:"white",
        backgroundColor: "transparent",
        top : '10vh',
        left : '31.5vw',
    }

    const signup = {
        position : "relative",
        top : "5vh",
    }

    const username_fill = {
        position : "relative",
        top : "5vh",
        left : "0vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const email_fill = {
        position : "relative",
        top : "5vh",
        left : "0vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const password_fill = {
        position : "relative",
        top : "5vh",
        left : "0vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const confirm_fill = {
        position : "relative",
        top : "5vh",
        left : "0vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
        bottom : "0vh",
    }

    const signup_butt = {
        position : "relative",
        top : "0vh",
        left : "0vw",
        height : "3vh",
        width : "10vw",
        backgroundColor: "#3533cd",
        borderRadius : "20px",
        alignItems : "center",
        border : "none",
        justifyContent : "center",
    }

    const small = {
        position : "relative",
        top : "12vh",
        left : "0vw",
    }

    const butt_text = {
        position : "relative",
        top : "-1vh",
    }

    const header = {
        position : "relative",
        top : "10vh",
        left : "44vw",
        color : "white",
        height : '5vh',
        width : '20vw',
    }
    
      const small_header = {
        position : "relative",
        top : "10vh",
        left : "43.7vw",
        color : "white",
        height : '5vh',
        width : '20vw',
    }

    const img_container = {
        position : "absolute",
        height : "20vh",
        width : "10vw",
        top : "70vh",
        left : "85vw",
    };

    const img_style = {
        maxWidth : "20vh",
        maxHeight : "20vh",
        display : "block",
    };

    const menu = {
        position : "absolute",
        top : "0vh",
        width: "15vw",
        left: "85vw",
        height: "100vh",
        padding: "0px",
        backgroundImage: "linear-gradient(to left, #000000 1%, #737373)",
        overflow: "hidden",
        transition: "width 0.00001s ease-in",
    };

    const name = {
        position : "relative",
        color : "white",
        top : "32.5vh",
        fontSize : "24px"
    };

    const img_clicked = {
        maxHeight: "100%",
        maxWidth : "100%",
    };

    const div_fill = {
        position : "relative",
        height : "1vh",
        top : "5vh",
    };

    return (

        /*styling*/
        <div style={main_div}>
            <h1 style = {header}>Enhance AI</h1>
            <p style = {small_header}>Enhance images using AI</p>
            <div style={form_div}>

                {show?       //to show the alersts, if show is true
                    <>
                      <Alert variant="success" onClose={() => {setShow(false)}} dismissible>
                            <p>
                                {serverResponse}
                            </p></Alert>
                        <h1 style = {signup}>SignUp</h1>
                    </>
                    :
                    <h1 style = {signup}>SignUp</h1>}
                <form>
                    <Form.Group>
                        <Form.Control style = {username_fill} placeholder='  Username'
                            type="text"
                            {...register("username", { required: true, maxLength: 25 })}
                        />
                    </Form.Group>
                    {errors.username?.type === "required" ? <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw"}}>Username is required</small> : <div style = {div_fill}></div>}
                    {errors.username?.type === "maxLength" ? <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw" }}>Max characters should be 25</small> : <div style = {div_fill}></div>}
                    
                    <Form.Group>
                        <Form.Control style = {email_fill} placeholder='  Email'
                            type="email"
                            {...register("email", { required: true, maxLength: 80 })}
                        />
                    </Form.Group>
                    {errors.email?.type === "required" ? <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw" }}>Email is required</small> : <div style = {div_fill}></div>}
                    {errors.email?.type === "maxLength" ? <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw"}}>Max characters should be 80</small> : <div style = {div_fill}></div>}
                    
                    <Form.Group>
                        <Form.Control style = {password_fill} placeholder='  Password'
                            type="password"
                            {...register("password", { required: true, minLength: 8 })}
                        />
                    </Form.Group>
                    {errors.password?.type === "required" ? <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw" }}> Password is required</small> : <div style = {div_fill}></div>}
                    {errors.password?.type === "minLength" ? <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw"}}>Minimum characters should be 8</small> : <div style = {div_fill}></div>}
                    
                    <Form.Group>
                        <Form.Control style = {confirm_fill} placeholder='  Confirm Password'
                            type="password"
                            {...register("confirmPassword", { required: true, minLength: 8 })}
                        />
                    </Form.Group>
                    {errors.confirmPassword?.type === "required" && <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw", bottom : "0vw", }}>Confirm password is required</small>}
                    {errors.confirmPassword?.type === "minLength" && <small style={{ position : "relative", color: "red", top : "5vh", left : "0vw" }}>Minimum characters should be 8</small>}
                    
                    <Form.Group className = "signup_form_butt">
                        <button className = "signup_butt" as="sub" variant="primary" onClick={handleSubmit(submitForm)}>Sign Up</button>
                    </Form.Group>
                    
                    <Form.Group>
                        <small style = {small}>
                            Already have an account?
                            <a className = "linklog" href='/login'>Log In</a>
                        </small>
                    </Form.Group>
                </form>
            </div>
            <div style = {img_container}>
              <img src = {image1} style = {img_style} alt = "robot_image"/>
            </div>
            <button className = "menu_button" onClick={handleButtonClick}><img style = {img_clicked} src = {triple_clicked} alt = "menu-button"></img></button>
            {isOpen && (<div style = {menu}>
            <button className = "menu_button_clicked" onClick={handleButtonClick}><img style = {img_clicked} src = {triple_clicked} alt = "menu-button"></img></button>
              <p style = {name}>Name</p>
              <p style = {name}>Home</p>
              <p style = {name}>Instructions</p>
            </div>)}
        </div>
    );
}

export default SignUpPage;
