import React from 'react'
import image1 from "../image.png";
import triple_clicked from "../triple_clicked.png"
import { useState, useRef } from 'react';
import './Upload.css'
import { authFetch } from '../auth';

const Upload = () => {
  const [image, setImage] = useState(null);
  const [filename, setfilename] = useState("");
  const [actualimage, setActual] = useState(null);
  const [uploaded, setUploaded] = useState(null);
  const [clicked, setClicked] = useState(false);
  const token = localStorage.getItem('access_token');
  const [videofile, setVideo] = useState(null);
  const [videourl, setVideoURL] = useState(null);
  const [videoname, setVideoName] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const inputRef = useRef();
  const videoRef = useRef();
  const [pausedFrame, setPausedFrame] = useState();
  const [prevFrame, setPrevFrame] = useState();
  const [nextFrame, setNextFrame] = useState();
  const [maxHeight, setMaxHeight] = useState(null);
  const containerRef = React.createRef();

  const expandDivIfNeeded = () => {
    const container = containerRef.current;
    if (container.scrollHeight > container.clientHeight) {
      setMaxHeight(container.scrollHeight + 'px');
    } else {
      setMaxHeight(null);
    }
  };

  useEffect(() => {
    window.addEventListener('resize', expandDivIfNeeded);
    expandDivIfNeeded(); // Initial check
    return () => {
      window.removeEventListener('resize', expandDivIfNeeded);
    };
  }, []);

  const handleChoose = () => {
    inputRef.current.click();
  };

  const handleVideoPause = () => {
    const video = videoRef.current;
    const currentTime = video.currentTime;

    // Only capture frames if the video is paused and not seeking
    if (video.paused && !video.seeking) {
      const canvas = document.createElement("canvas");
      canvas.width = video.videoWidth;
      canvas.height = video.videoHeight;
      const context = canvas.getContext("2d");

      // Capture the frame at the paused second
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const pausedFrameURI = canvas.toDataURL();
      setPausedFrame(pausedFrameURI);

      // Calculate the time for frames before and after the paused second
      const prevTime = Math.max(currentTime - 1, 0);
      const nextTime = Math.min(currentTime + 1, video.duration);

      // Seek to the time for the previous frame and capture it
      video.currentTime = prevTime;
      video.onseeked = () => {
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        const prevFrameURI = canvas.toDataURL();
        setPrevFrame(prevFrameURI);

        // Seek to the time for the next frame and capture it
        video.currentTime = nextTime;
        video.onseeked = () => {
          context.drawImage(video, 0, 0, canvas.width, canvas.height);
          const nextFrameURI = canvas.toDataURL();
          setNextFrame(nextFrameURI);

          // Restore the original video time
          video.currentTime = currentTime;
          video.onseeked = null; // Reset the onseeked handler
        };
      };
    }
  };

  const handleButtonClick = () => {
    setIsOpen(!isOpen);
  };

  const div1_style = {
    position: "relative",
    backgroundImage: "linear-gradient(to right, #000000 5%, #3533cd)",
    height: "100vh",
    width: "100vw",
    top: "-2.5vh",
  };

  const h1_style = {
      position : "relative",
      color : "white",
      top : "5vh",
      textAlign : "center",
  };

  const p_style = {
    position : "relative",
    color : "white",
    top : "4vh",
    textAlign : "center",
  };

  const upload_div = {
    position : "relative",
    border: "3px dotted white",
    borderRadius : "40px",
    height : "74.38vh",
    width : "69.19vw",
    top : "7vh",
    left : "15vw",
  };

  const button1_style = {
    position : "relative",
    borderRadius : "25px",
    border : "0px",
    top : "25vh",
    left : "30.7vw",
    height : "5vh",
    width : "15vh",
    backgroundColor : "white",
  };

  const small_p = {
    position : "relative",
    color : "white",
    top : "25vh",
    fontSize : "10px",
    textAlign : "center",
  };

  const img_container = {
      position : "relative",
      height : "20vh",
      width : "10vw",
      top : "-7vh",
      left : "88vw",
  };

  const img_style = {
      maxWidth : "20vh",
      maxHeight : "20vh",
      display : "block",
  };

  /*const triple_img = {
      maxHeight : "100%",
      maxWidth : "100%",
  };*/

  const menu = {
    position : "relative",
    top : "-109.5vh",
    width: "15vw",
    left: "85vw",
    height: "100vh",
    padding: "0px",
    backgroundImage: "linear-gradient(to left, #000000 1%, #737373)",
    overflow: "hidden",
    transition: "width 0.00001s ease-in",
  };

  const img_clicked = {
    maxHeight: "100%",
    maxWidth : "100%",
  };

  const name = {
    position : "relative",
    color : "white",
    top : "12.5vh",
    fontSize : "24px"
  };

  const form_p = {
    position : "relative",
    top : "1.5vh",
  };

  const image_uploaded = {
    maxHeight : "100%",
    maxWidth : "100%",
    minHeight : "100%",
    minWidth : "100%",
    borderRadius : "40px",
  };

  const handleUpload = async () => {
    setClicked(!clicked);
    console.log(token);
    if (actualimage) {
      try {
        const formData = new FormData();
        formData.append('image', actualimage);

        console.log(formData);

        const response = await authFetch('http://localhost:5000/upload/submit', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
          },
          body: formData,
        });

        if (response.ok) {
          console.log("SENT!")
          //setActual(null); // Clear selected image
          console.log(response)

          getUploaded();
