  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      minHeight: "100vh",
    },
    uploadBox: {
      backgroundColor: "rgba(255, 255, 255, 0.8)",
      padding: "10px",
      borderRadius: "5px",
      textAlign: "center",
      boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.2)",
      width: "50%", // Adjust the width based on your preference
      maxWidth: "40%", // Maximum width for larger screens
      height: "50%",
    },
    uploadButton: {
      backgroundColor: "#007bff",
      color: "white",
      border: "none",
      padding: "10px 20px",
      cursor: "pointer",
      marginTop: "20px",
    },
    video: {
      marginTop: "5px",
      width: "35vw",
      height: "50vh",
    },
    framesRow: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      marginTop: "10px",
    },
    frameImage: {
      maxWidth: "30%",
      border: "1px solid #ccc",
      margin: "0 5px",
    },
  };

  localStorage.setItem('filename', filename)

  const path = "/enhance"
  return (
    <body>
      <div  ref={containerRef} style = {div1_style}>
        <h1 style = {h1_style}>Enhance Ai</h1>
        <p style = {p_style}>Upscale your images using Ai</p>
        <div style = {upload_div}>
          {image || videourl ? 
          <>{image ? <>
          <img style = {image_uploaded} src = {image} alt = "HI"/>
          {uploaded ? <a href = {path}><button className = "enhance">Enhance</button></a> : 
           <><button className = "submit" onClick = {handleUpload}>Submit</button>{clicked ? <><div className = "spinner_upload"></div><p className = "loading">Loading</p></> : console.log("NO")}</>}
           </>:<>
               <video
                ref={videoRef}
                style={video_player}
                controls
                src={videourl}
                onPause={handleVideoPause}
              />
               {/*<video style = {video_player} controls>
                 <source src={videourl} type="video/mp4"/>
