               <a href = "/video"><button className = "submit">Submit</button></a>*/}
               <div style={styles.framesRow}>
              {pausedFrame && (
                <div>
                  <h2>Frames:</h2>
                  <div style={styles.framesRow}>
                    <img
                      style={styles.frameImage}
                      src={prevFrame}
                      alt="Frame Before"
                    />
                    <img style={styles.frameImage} src={pausedFrame} alt="Paused Frame" />
                    <img style={styles.frameImage} src={nextFrame} alt="Frame After" />
                  </div>
                </div>
              )}
              </div>
               {console.log(localStorage.getItem('videoname'))}
          </>}</>
          :
          <>
          <form className = "form_style" onClick={() => document.querySelector(".input-field").click()}>
