import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import "./SignUp.css"


const SignUpPage = () => {


    const { register, handleSubmit, reset, formState: { errors } } = useForm();
    const [show, setShow] = useState(false);
    const [serverResponse, setServerResponse]=useState('');


    const submitForm = (data) => {
        /*to check if the password and confirmPassword are equal*/
        if (data.password === data.confirmPassword) {

            const body = {
                username: data.username,
                email: data.email,
                password: data.password
            }
            /*send the data to server*/
            const requestOptions = {
                method: "POST",
                /*data will be in json format*/
                headers: {
                    'content-type': 'application/json'
                },

                /*body that we are going to send with this data*/
                /*stringify converts the object into json*/
                body: JSON.stringify(body)

            }

            fetch('/auth/signup', requestOptions)
                .then(res => res.json())
                .then(data => {
                    console.log(data)
                    setServerResponse(data.message)
                    console.log(serverResponse)
                    setShow(true)
                })
                .catch(err => console.log(err))

            reset()
        }

        else {

            alert("Passwords do not match")
        }
    }


    const main_div = {
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '100vh',
            backgroundImage: "linear-gradient(to right, #000000 5%, #3533cd)", 
            backgroundSize: '200vw 200vh',
            backgroundAttachment: 'fixed',
        }

    const form_div = {
        position : "relative",
        height : "50vh",
        border : "5px dotted white",
        borderRadius: '40px',
        textAlign: 'center',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
        width: "600px",
        color:"white",
        backgroundColor: "transparent",
    }

    const signup = {
        position : "relative",
        top : "5vh",
    }

    const username_fill = {
        position : "fixed",
        top : "40vh",
        left : "42.5vw",
        height : "4vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const email_fill = {
        position : "fixed",
        top : "46vh",
        left : "42.5vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const password_fill = {
        position : "fixed",
        top : "52vh",
        left : "42.5vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const confirm_fill = {
        position : "fixed",
        top : "58vh",
        left : "42.5vw",
        height : "3vh",
        width : "15vw",
        borderRadius : "20px",
        border : "1px solid black",
    }

    const signup_butt = {
        position : "fixed",
        top : "65vh",
        left : "45.25vw",
        height : "3vh",
        backgroundColor: "#3533cd",
        width : "10vw",
        borderRadius : "20px",
        alignItems : "center",
        justifyContent : "center",
    }

    const small = {
        position : "fixed",
        top : "70vh",
        left : "45vw",
    }

    const butt_text = {
        position : "relative",
        top : "-0.75vh",
    }
    return (

        /*styling*/
        <div style={main_div}>
            <div style={form_div}>

                {show?       //to show the alersts, if show is true
                    <>
                      <Alert variant="success" onClose={() => {setShow(false)}} dismissible>
                            <p>
                                {serverResponse}
                            </p></Alert>
                        <h1 style = {signup}>SignUp</h1>
                    </>
                    :
                    <h1 style = {signup}>SignUp</h1>}
                <form>
                    <Form.Group>
                        {/*<Form.Label style = {username}>Username</Form.Label>*/}
                        <Form.Control style = {username_fill} placeholder='Username'
                            type="text"
                            {...register("username", { required: true, maxLength: 25 })}
                        />
                    </Form.Group>
                    {errors.username?.type === "required" && <small style={{ position : "fixed", color: "red", top : "43.5vh", left : "46.75vw"}}>Username is required</small>}
                    {errors.username?.type === "maxLength" && <small style={{ position : "fixed", color: "red", top : "43.5vh", left : "45.75vw" }}>Max characters should be 25</small>}

                    <Form.Group>
                        {/*<Form.Label style = {email}>Email</Form.Label>*/}
                        <Form.Control style = {email_fill} placeholder='Email'
                            type="email"
                            {...register("email", { required: true, maxLength: 80 })}
                        />
                    </Form.Group>
                    {errors.email?.type === "required" && <small style={{ position : "fixed", color: "red", top : "49.5vh", left : "47.5vw" }}>Email is required</small>}
                    {errors.email?.type === "maxLength" && <small style={{ position : "fixed", color: "red", top : "49.5vh", left : "45.75vw"}}>Max characters should be 80</small>}

                    <Form.Group>
                        {/*<Form.Label style = {password}>Password</Form.Label>*/}
                        <Form.Control style = {password_fill} placeholder='Password'
                            type="password"
                            {...register("password", { required: true, minLength: 8 })}
                        />
                    </Form.Group>
                    {errors.password?.type === "required" && <small style={{ position : "fixed", color: "red", top : "55.5vh", left : "47vw" }}> Password is required</small>}
                    {errors.password?.type === "minLength" && <small style={{ position : "fixed", color: "red", top : "55.5vh", left : "45vw"}}>Minimum characters should be 8</small>}

                    <Form.Group>
                        {/*<Form.Label style = {password}>Confirm Password</Form.Label>*/}
                        <Form.Control style = {confirm_fill} placeholder='Confirm Password'
                            type="password"
                            {...register("confirmPassword", { required: true, minLength: 8 })}
                        />
                    </Form.Group>
                    {errors.confirmPassword?.type === "required" && <small style={{ position : "fixed", color: "red", top : "61.5vh", left : "45.75vw" }}>Confirm password is required</small>}
                    {errors.confirmPassword?.type === "minLength" && <small style={{ position : "fixed", color: "red", top : "61.5vh", left : "45vw" }}>Minimum characters should be 8</small>}

                    <br></br>
                    <Form.Group>
                        <Button className = "signup_butt" as="sub" variant="primary" onClick={handleSubmit(submitForm)}><p style = {butt_text}>SignUp</p></Button>
                    </Form.Group>
                    <br></br>
                    <Form.Group>
                        <small style = {small}>
                            Already have an account?
                            <Link to='/login'>Log In</Link>
                        </small>
                    </Form.Group>
                </form>
            </div>
        </div>
    );
}

export default SignUpPage;
